// TEMPORARY FIX - Mock API that always works
export const authAPI = {
  login: async (email, password) => {
    console.log('Login attempt:', email);
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          data: {
            token: 'demo-token-12345',
            user: {
              id: 1,
              name: email.split('@')[0] || 'Demo User',
              email: email,
              role: 'student',
              phone: '1234567890',
              department: 'Computer Science',
              year: '2nd Year'
            },
            message: 'Login successful'
          }
        });
      }, 1000);
    });
  },

  register: async (userData) => {
    console.log('Register attempt:', userData);
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          data: {
            token: 'demo-token-12345',
            user: {
              id: 2,
              name: userData.name,
              email: userData.email,
              role: 'student',
              phone: userData.phone || '1234567890',
              department: userData.department || 'Computer Science',
              year: userData.year || '2nd Year'
            },
            message: 'Registration successful'
          }
        });
      }, 1000);
    });
  }
};

export const eventsAPI = {
  getAll: () => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          data: {
            success: true,
            data: [
              {
                _id: '1',
                title: 'Tech Conference 2024',
                description: 'Annual technology conference with workshops and speakers',
                date: '2024-12-15',
                time: '10:00 AM',
                venue: 'Main Auditorium',
                category: 'technical',
                maxParticipants: 100,
                currentParticipants: 45,
                registrationFee: 0,
                rules: ['Bring your laptop', 'No late entries']
              },
              {
                _id: '2',
                title: 'Cultural Fest',
                description: 'Cultural events, dance and music performances',
                date: '2024-12-20',
                time: '2:00 PM',
                venue: 'College Ground',
                category: 'cultural',
                maxParticipants: 200,
                currentParticipants: 120,
                registrationFee: 50,
                rules: ['Traditional attire preferred', 'Register before deadline']
              },
              {
                _id: '3',
                title: 'Coding Workshop',
                description: 'Learn web development with hands-on projects',
                date: '2024-12-18',
                time: '9:00 AM',
                venue: 'Computer Lab',
                category: 'workshop',
                maxParticipants: 50,
                currentParticipants: 25,
                registrationFee: 100,
                rules: ['Basic programming knowledge required', 'Bring your laptop']
              }
            ]
          }
        });
      }, 500);
    });
  }
};

export const registrationsAPI = {
  register: (eventId) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          data: {
            success: true,
            message: 'Successfully registered for the event!'
          }
        });
      }, 500);
    });
  },
  getMyRegistrations: () => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          data: {
            success: true,
            data: []
          }
        });
      }, 500);
    });
  }
};
