import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    const result = await login(formData.email, formData.password);
    
    if (result.success) {
      setMessage('‚úÖ Login successful! Redirecting...');
      setTimeout(() => {
        navigate('/', { replace: true });
      }, 1000);
    } else {
      setMessage('‚ùå ' + result.message);
    }
    
    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.header}>
          <div style={styles.iconCircle}>üîê</div>
          <h2 style={styles.title}>Welcome Back</h2>
          <p style={styles.subtitle}>Sign in to continue to EventHub</p>
        </div>
        
        {message && (
          <div style={{
            padding: '14px 18px',
            borderRadius: '10px',
            marginBottom: '24px',
            background: message.includes('‚úÖ') ? 'linear-gradient(135deg, #d4edda, #c3e6cb)' : 'linear-gradient(135deg, #f8d7da, #f5c6cb)',
            color: message.includes('‚úÖ') ? '#155724' : '#721c24',
            border: `2px solid ${message.includes('‚úÖ') ? '#28a745' : '#dc3545'}`,
            fontWeight: '500',
            boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
          }}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.inputGroup}>
            <span style={styles.inputIcon}>üìß</span>
            <input
              style={styles.input}
              type="email"
              name="email"
              placeholder="Email Address"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div style={styles.inputGroup}>
            <span style={styles.inputIcon}>üîë</span>
            <input
              style={styles.input}
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          <button 
            type="submit" 
            style={styles.button}
            disabled={loading}
            onMouseOver={(e) => !loading && (e.target.style.transform = 'translateY(-2px)')}
            onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
          >
            {loading ? '‚è≥ Signing in...' : 'üöÄ Sign In'}
          </button>
        </form>

        <div style={styles.demoBox}>
          <div style={{fontSize: '13px', fontWeight: '700', color: '#667eea', marginBottom: '10px', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '6px'}}>
            <span>üí°</span> Demo Account
          </div>
          <div style={{fontSize: '14px', color: '#4a5568', lineHeight: '1.6'}}>
            <strong>Email:</strong> admin@example.com<br/>
            <strong>Password:</strong> admin123
          </div>
        </div>

        <div style={styles.divider}>
          <span style={styles.dividerText}>or</span>
        </div>

        <div style={styles.link}>
          Don't have an account?{' '}
          <Link to="/register" style={styles.linkText}>Create one here</Link>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    padding: '20px',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
  },
  card: {
    background: 'rgba(255,255,255,0.98)',
    padding: '45px 40px',
    borderRadius: '20px',
    boxShadow: '0 30px 80px rgba(0,0,0,0.3), 0 0 1px rgba(0,0,0,0.05)',
    maxWidth: '440px',
    width: '100%',
    backdropFilter: 'blur(10px)',
    border: '1px solid rgba(255,255,255,0.2)'
  },
  header: {
    textAlign: 'center',
    marginBottom: '35px'
  },
  iconCircle: {
    width: '70px',
    height: '70px',
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '32px',
    margin: '0 auto 20px',
    boxShadow: '0 10px 30px rgba(102,126,234,0.4)'
  },
  title: {
    color: '#2d3748',
    marginBottom: '10px',
    fontSize: '28px',
    fontWeight: '700',
    letterSpacing: '-0.5px'
  },
  subtitle: {
    color: '#718096',
    fontSize: '15px',
    margin: 0
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '18px'
  },
  inputGroup: {
    position: 'relative',
    display: 'flex',
    alignItems: 'center'
  },
  inputIcon: {
    position: 'absolute',
    left: '16px',
    fontSize: '20px',
    zIndex: 1,
    pointerEvents: 'none'
  },
  input: {
    width: '100%',
    padding: '14px 16px 14px 50px',
    border: '2px solid #e2e8f0',
    borderRadius: '12px',
    fontSize: '15px',
    color: '#2d3748',
    background: '#fff',
    transition: 'all 0.3s ease',
    fontFamily: 'inherit'
  },
  button: {
    padding: '16px',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    color: 'white',
    border: 'none',
    borderRadius: '12px',
    fontSize: '16px',
    fontWeight: '700',
    cursor: 'pointer',
    marginTop: '10px',
    boxShadow: '0 10px 30px rgba(102,126,234,0.3)',
    transition: 'all 0.3s ease',
    textTransform: 'uppercase',
    letterSpacing: '0.5px'
  },
  demoBox: {
    marginTop: '24px',
    padding: '18px',
    background: 'linear-gradient(135deg, #f7fafc, #edf2f7)',
    borderRadius: '12px',
    border: '2px solid #e2e8f0',
    textAlign: 'center'
  },
  divider: {
    position: 'relative',
    textAlign: 'center',
    margin: '28px 0 24px',
    borderTop: '1px solid #e2e8f0'
  },
  dividerText: {
    position: 'relative',
    top: '-12px',
    background: 'rgba(255,255,255,0.98)',
    padding: '0 15px',
    color: '#a0aec0',
    fontSize: '13px',
    fontWeight: '600'
  },
  link: {
    color: '#718096',
    textAlign: 'center',
    fontSize: '14px'
  },
  linkText: {
    color: '#667eea',
    textDecoration: 'none',
    fontWeight: '700',
    transition: 'color 0.2s'
  }
};

export default Login;
