import React, { useState, useEffect } from 'react';
import { eventsAPI, registrationsAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    loadEvents();
  }, []);

  const loadEvents = async () => {
    try {
      const response = await eventsAPI.getAll();
      // Debug: See what events are returned (helpful when API shape differs)
      console.log('Events API response (raw):', response);
      const payload = response?.data;
      if (payload?.success === false) {
        // backend returned an error payload
        const msg = payload?.message || 'Failed to load events';
        setError(msg);
        setEvents([]);
      } else {
        // support multiple payload shapes
        const eventsArray = payload?.data ?? payload?.events ?? (Array.isArray(payload) ? payload : []);
        console.log('Parsed events array length:', eventsArray.length);
        setEvents(eventsArray || []);
      }
    } catch (error) {
      console.error('Error loading events:', error, error.response?.data);
      const message = error.response?.data?.message || 'Failed to load events';
      setError(message);
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (eventId) => {
    try {
      await registrationsAPI.register(eventId);
      alert('üéâ Successfully registered for the event!');
      loadEvents(); // Refresh events to update participant count
    } catch (error) {
      const message = error.response?.data?.message || 'Registration failed';
      alert(`‚ùå ${message}`);
    }
  };

  if (loading) return <div style={styles.loading}>Loading events...</div>;
  if (error) return <div style={styles.error}>{error}</div>;

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Upcoming Events</h1>
      <div style={styles.eventsGrid}>
        {events.length === 0 ? (
          <div style={styles.noEvents}>No events found.</div>
        ) : (
          events.map(event => (
            <div key={event._id} style={styles.eventCard}>
              {/* Optional event image */}
              {event.imageUrl && (
                <img
                  src={event.imageUrl}
                  alt={event.title}
                  style={styles.eventImage}
                />
              )}
              <h3 style={styles.eventTitle}>{event.title}</h3>
              <p style={styles.eventDescription}>{event.description}</p>
              
              <div style={styles.eventDetails}>
                <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
                <p><strong>Time:</strong> {event.time}</p>
                <p><strong>Venue:</strong> {event.venue}</p>
                <p><strong>Category:</strong> {event.category}</p>
                <p><strong>Participants:</strong> {event.currentParticipants}/{event.maxParticipants}</p>
                {event.registrationFee > 0 && (
                  <p><strong>Fee:</strong> ‚Çπ{event.registrationFee}</p>
                )}
              </div>

              {event.rules && event.rules.length > 0 && (
                <div style={styles.eventRules}>
                  <strong>Rules:</strong>
                  <ul>
                    {event.rules.map((rule, index) => (
                      <li key={index}>{rule}</li>
                    ))}
                  </ul>
                </div>
              )}

              {user?.role === 'student' && (
                <button
                  style={{
                    ...styles.registerButton,
                    ...(event.currentParticipants >= event.maxParticipants ? styles.disabledButton : {})
                  }}
                  onClick={() => handleRegister(event._id)}
                  disabled={event.currentParticipants >= event.maxParticipants}
                >
                  {event.currentParticipants >= event.maxParticipants ? 'üéüÔ∏è Event Full' : 'üìù Register Now'}
                </button>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Events;

const styles = {
  container: {
    padding: '32px',
    minHeight: '100vh',
    background: 'rgba(255,255,255,0.05)',
  },
  title: {
    fontSize: '2.2rem',
    marginBottom: '24px',
    color: '#ff61ba',
    textAlign: 'center',
    fontWeight: 700,
    letterSpacing: '1px',
  },
  eventsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(340px, 1fr))',
    gap: '32px',
  },
  eventCard: {
    background: 'rgba(255,255,255,0.13)',
    borderRadius: '16px',
    padding: '24px',
    boxShadow: '0 2px 16px rgba(0,0,0,0.08)',
    color: '#333',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    minHeight: '350px',
  },
  eventImage: {
    width: '100%',
    borderRadius: '8px',
    marginBottom: '12px',
    objectFit: 'cover',
    maxHeight: '180px',
  },
  eventTitle: {
    fontSize: '1.4rem',
    color: '#fcb045',
    marginBottom: '8px',
    fontWeight: 600,
  },
  eventDescription: {
    fontSize: '1rem',
    marginBottom: '12px',
    color: '#444',
  },
  eventDetails: {
    fontSize: '0.98rem',
    marginBottom: '12px',
    color: '#555',
  },
  eventRules: {
    fontSize: '0.95rem',
    marginBottom: '12px',
    color: '#666',
  },
  registerButton: {
    background: 'linear-gradient(90deg, #ff61ba, #fcb045)',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    padding: '10px 22px',
    fontSize: '1rem',
    fontWeight: 600,
    cursor: 'pointer',
    marginTop: '10px',
    transition: 'opacity 0.2s',
  },
  disabledButton: {
    opacity: 0.6,
    cursor: 'not-allowed',
  },
  loading: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '60vh',
    fontSize: '1.2rem',
    color: '#ff61ba',
  },
  error: {
    color: '#d32f2f',
    textAlign: 'center',
    marginTop: '32px',
    fontSize: '1.1rem',
  },
  noEvents: {
    textAlign: 'center',
    color: '#888',
    fontSize: '1.1rem',
    gridColumn: '1/-1',
    padding: '40px 0',
  },
};
