import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    const result = await login(formData.email, formData.password);
    
    if (result.success) {
      setMessage('‚úÖ Login successful! Redirecting...');
      setTimeout(() => {
        navigate('/', { replace: true });
      }, 1000);
    } else {
      setMessage('‚ùå ' + result.message);
    }
    
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        {/* Left side - Branding/Info */}
        <div className="auth-header">
          <div className="auth-logo">EventHub</div>
          <h2 className="auth-title">Welcome Back</h2>
          <p className="auth-subtitle">Sign in to access your account, discover upcoming events, and manage your registrations seamlessly.</p>
          
          <div style={{marginTop:28,padding:'22px 26px',background:'rgba(124,58,237,0.04)',borderRadius:12,border:'1px solid rgba(124,58,237,0.1)'}}>
            <div style={{fontSize:'0.9rem',color:'var(--muted)',lineHeight:1.8}}>
              <div style={{marginBottom:10,color:'var(--accent-2)',fontWeight:700}}>üí° Quick Demo Access</div>
              Use any email and password to quickly explore the platform!
            </div>
          </div>
        </div>

        {/* Right side - Login Form */}
        <div>
          {message && (
            <div className={`auth-message ${message.includes('‚úÖ') ? 'success' : 'error'}`}>
              {message}
            </div>
          )}

          <form onSubmit={handleSubmit} className="auth-form">
            <div>
              <label className="form-label">Email address</label>
              <div style={{position:'relative'}}>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="form-input"
                  placeholder="you@example.com"
                  required
                  style={{paddingLeft:44}}
                />
                <span style={{position:'absolute',left:16,top:'50%',transform:'translateY(-50%)',fontSize:'1.2rem'}}>üìß</span>
              </div>
            </div>

            <div>
              <label className="form-label">Password</label>
              <div style={{position:'relative'}}>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  className="form-input"
                  placeholder="Enter your password"
                  required
                  style={{paddingLeft:44}}
                />
                <span style={{position:'absolute',left:16,top:'50%',transform:'translateY(-50%)',fontSize:'1.2rem'}}>üîí</span>
              </div>
            </div>

            <button 
              type="submit" 
              className="auth-btn"
              disabled={loading}
              style={{marginTop:12}}
            >
              {loading ? '‚è≥ Signing in...' : 'üöÄ Sign in to EventHub'}
            </button>
          </form>

          <div style={{textAlign:'center',margin:'24px 0 0',padding:'18px 0 0',borderTop:'1px solid rgba(255,255,255,0.06)'}}>
            <p style={{color:'var(--muted)',fontSize:'0.92rem',margin:0}}>
              Don't have an account?{' '}
              <Link to="/register" style={{color:'var(--accent-1)',textDecoration:'none',fontWeight:700}}>
                Create one here
              </Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
