import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
  const [email, setEmail] = useState('test@example.com');
  const [password, setPassword] = useState('test123');
  const [loading, setLoading] = useState(false);
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    console.log('üîÑ Attempting login...');
    
    const result = await login(email, password);
    
    console.log('üìù Login result:', result);
    
    if (result.success) {
      console.log('‚úÖ Login successful, redirecting...');
      setTimeout(() => {
        navigate('/');
      }, 1000);
    } else {
      console.log('‚ùå Login failed:', result.message);
      alert('Login failed: ' + result.message);
    }
    
    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2>üé™ EventHub Login</h2>
        <p style={styles.subtitle}>Enter ANY email and password - it will work!</p>
        
        <form onSubmit={handleSubmit} style={styles.form}>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter ANY email"
            style={styles.input}
            required
          />
          
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter ANY password"
            style={styles.input}
            required
          />
          
          <button type="submit" style={styles.button} disabled={loading}>
            {loading ? 'Logging in...' : 'Login Now'}
          </button>
        </form>
        
        <p style={styles.demoText}>
          <strong>Demo:</strong> Use any email/password combination
        </p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    padding: '20px',
    background: 'linear-gradient(135deg, #ff61ba, #fcb045)',
  },
  card: {
    background: 'rgba(255,255,255,0.95)',
    padding: '40px',
    borderRadius: '15px',
    boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
    width: '100%',
    maxWidth: '400px',
    textAlign: 'center',
  },
  subtitle: {
    color: '#666',
    marginBottom: '30px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
  },
  input: {
    padding: '15px',
    border: '2px solid #ddd',
    borderRadius: '8px',
    fontSize: '16px',
  },
  button: {
    padding: '15px',
    background: '#ff61ba',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    fontWeight: 'bold',
    cursor: 'pointer',
  },
  demoText: {
    marginTop: '20px',
    color: '#666',
    fontSize: '14px',
  }
};

export default Login;
