import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { eventsAPI } from '../services/api';
import TestConnection from '../components/TestConnection';

const Home = () => {
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadEvents();
  }, []);

  const loadEvents = async () => {
    try {
      const response = await eventsAPI.getAll();
      setEvents(response.data.data.slice(0, 3)); // Show only 3 events on home
    } catch (error) {
      console.error('Error loading events:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = (eventTitle) => {
    alert(`This would register you for: ${eventTitle}`);
    // In next step, this will actually register the user
  };

  return (
    <div>
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1 className="hero-title">
            Welcome to <span className="gradient-text">EventHub</span>
          </h1>
          <p className="hero-subtitle">
            Discover amazing events from our database! Real data from MongoDB.
          </p>
          
          {/* Connection Test */}
          <TestConnection />
        </div>
      </section>

      {/* Featured Events from Database */}
      <section className="features">
        <h2 className="section-title">Featured Events</h2>
        {loading ? (
          <p style={{textAlign: 'center', color: 'white'}}>Loading events...</p>
        ) : (
          <div className="features-grid">
            {events.map(event => (
              <div key={event._id} className="feature-card">
                <div className="feature-icon">üé™</div>
                <h3>{event.title}</h3>
                <p>{event.description}</p>
                <div style={{textAlign: 'left', margin: '10px 0', fontSize: '14px', color: '#666'}}>
                  <p>üìç <strong>Venue:</strong> {event.venue}</p>
                  <p>üóìÔ∏è <strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
                </div>
                <button 
                  className="feature-btn"
                  onClick={() => handleRegister(event.title)}
                >
                  Register Now
                </button>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Quick Actions */}
      <section className="quick-actions">
        <h2 className="section-title">Quick Actions</h2>
        <div className="action-buttons">
          <button className="action-btn primary" onClick={() => navigate('/events')}>
            üé™ Browse All Events
          </button>
          <button className="action-btn secondary" onClick={() => navigate('/registrations')}>
            üìã My Registrations
          </button>
          <button className="action-btn success" onClick={() => navigate('/login')}>
            üîê Login / Register
          </button>
        </div>
      </section>
    </div>
  );
};

export default Home;
