import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
  timeout: 5000, // 5 second timeout
});

// Add token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Smart API functions that try real backend first, then fallback to mock
export const authAPI = {
  login: async (email, password) => {
    try {
      console.log('ðŸ” Attempting REAL login...');
      const response = await api.post('/auth/login', { email, password });
      console.log('âœ… Real login successful');
      return response;
    } catch (error) {
      console.log('âŒ Real login failed, using mock data');
      // Fallback to mock data
      return new Promise((resolve) => {
        setTimeout(() => {
          resolve({
            data: {
              token: 'fallback-token-' + Date.now(),
              user: {
                id: 1,
                name: email.split('@')[0] || 'Demo User',
                email: email,
                role: 'student',
                phone: '+1234567890',
                department: 'Computer Science',
                year: '2nd Year'
              },
              message: 'Login successful (mock data)'
            }
          });
        }, 1000);
      });
    }
  },

  register: async (userData) => {
    try {
      console.log('ðŸ‘¤ Attempting REAL registration...');
      const response = await api.post('/auth/register', userData);
      console.log('âœ… Real registration successful');
      return response;
    } catch (error) {
      console.log('âŒ Real registration failed, using mock data');
      // Fallback to mock data
      return new Promise((resolve) => {
        setTimeout(() => {
          resolve({
            data: {
              token: 'fallback-token-' + Date.now(),
              user: {
                id: Date.now(),
                name: userData.name,
                email: userData.email,
                role: 'student',
                phone: userData.phone || '+1234567890',
                department: userData.department || 'Computer Science',
                year: userData.year || '2nd Year'
              },
              message: 'Registration successful (mock data)'
            }
          });
        }, 1000);
      });
    }
  },

  getCurrentUser: () => {
    return api.get('/auth/me').catch(() => {
      // Fallback for getCurrentUser
      return { data: null };
    });
  }
};

// Similar hybrid approach for events and registrations
export const eventsAPI = {
  getAll: () => {
    return api.get('/events').catch(() => {
      // Fallback mock events
      return new Promise((resolve) => {
        setTimeout(() => {
          resolve({
            data: {
              success: true,
              data: [
                {
                  _id: '1',
                  title: 'Tech Conference 2024',
                  description: 'Annual technology conference with workshops and speakers',
                  date: '2024-12-15',
                  time: '10:00 AM',
                  venue: 'Main Auditorium',
                  category: 'technical',
                  maxParticipants: 100,
                  currentParticipants: 45,
                  registrationFee: 0
                }
              ]
            }
          });
        }, 500);
      });
    });
  }
};

export const registrationsAPI = {
  register: (eventId) => {
    return api.post('/registrations', { eventId }).catch(() => {
      return new Promise((resolve) => {
        setTimeout(() => {
          resolve({
            data: {
              success: true,
              message: 'Registered successfully (mock)'
            }
          });
        }, 500);
      });
    });
  },

  getMyRegistrations: () => {
    return api.get('/registrations/my-registrations').catch(() => {
      return new Promise((resolve) => {
        resolve({
          data: {
            success: true,
            data: []
          }
        });
      });
    });
  }
};

export default api;
