import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    email: 'demo@example.com', // Pre-filled for testing
    password: 'password123'    // Pre-filled for testing
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('üîÑ Logging in...');

    try {
      const result = await login(formData.email, formData.password);
      
      if (result.success) {
        setMessage('‚úÖ Login successful! Redirecting...');
        setTimeout(() => {
          navigate('/');
          window.location.reload(); // Force refresh
        }, 1500);
      } else {
        setMessage('‚ùå ' + result.message);
      }
    } catch (error) {
      setMessage('‚ùå Login failed: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Welcome Back to EventHub</h2>
        <p style={{ color: '#fff', marginBottom: '20px' }}>Sign in to your account</p>

        {message && (
          <div style={{
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            background: message.includes('‚úÖ') ? 'rgba(0,255,0,0.1)' : 'rgba(255,0,0,0.1)',
            color: '#fff',
            border: message.includes('‚úÖ') ? '1px solid #00ff00' : '1px solid #ff0000',
            textAlign: 'center'
          }}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Email Address</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter your password"
              required
            />
          </div>

          <button 
            type="submit" 
            className="auth-btn"
            disabled={loading}
          >
            {loading ? 'üîÑ Signing In...' : 'üöÄ Sign In'}
          </button>
        </form>

        <p style={{ color: '#fff', marginTop: '20px', textAlign: 'center' }}>
          Don't have an account?{' '}
          <a href="/register" style={{ color: '#ffd369', textDecoration: 'none', fontWeight: 'bold' }}>
            Sign up here
          </a>
        </p>

        <div style={{
          marginTop: '25px',
          padding: '15px',
          background: 'rgba(255,255,255,0.1)',
          borderRadius: '8px',
          fontSize: '14px',
          color: '#fff',
          textAlign: 'center'
        }}>
          <p><strong>üéØ Demo Instructions:</strong></p>
          <p>‚Ä¢ Use any email and password</p>
          <p>‚Ä¢ Example: demo@example.com / anypassword</p>
          <p>‚Ä¢ Click "Sign In" to login instantly</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
