import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    const result = await login(formData.email, formData.password);
    
    if (result.success) {
      setMessage('✅ Login successful! Redirecting...');
      // Force navigation to home page
      setTimeout(() => {
        navigate('/');
        window.location.reload(); // Force refresh to update context
      }, 1000);
    } else {
      setMessage('❌ ' + result.message);
    }
    
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Welcome Back</h2>
        <p style={{ color: '#fff', marginBottom: '20px' }}>Sign in to your account</p>

        {message && (
          <div style={{
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            background: message.includes('✅') ? 'rgba(0,255,0,0.1)' : 'rgba(255,0,0,0.1)',
            color: '#fff',
            border: message.includes('✅') ? '1px solid #00ff00' : '1px solid #ff0000'
          }}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Email Address</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter your password"
              required
            />
          </div>

          <button 
            type="submit" 
            className="auth-btn"
            disabled={loading}
          >
            {loading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>

        <p style={{ color: '#fff', marginTop: '20px' }}>
          Don't have an account?{' '}
          <a href="/register" style={{ color: '#ffd369', textDecoration: 'none' }}>
            Sign up here
          </a>
        </p>

        <div style={{
          marginTop: '25px',
          padding: '15px',
          background: 'rgba(255,255,255,0.1)',
          borderRadius: '8px',
          fontSize: '14px',
          color: '#fff'
        }}>
          <p><strong>Demo Credentials:</strong></p>
          <p>Any email and password will work!</p>
          <p>Try: demo@example.com / anypassword</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
