import React, { useState, useEffect } from 'react';
import { testAPI, eventsAPI } from '../services/api';

const TestConnection = () => {
  const [backendStatus, setBackendStatus] = useState('Testing...');
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    testConnection();
  }, []);

  const testConnection = async () => {
    try {
      // Test backend connection
      const testResponse = await testAPI.test();
      setBackendStatus(`‚úÖ Backend Connected: ${testResponse.data.message}`);
      
      // Load real events from database
      const eventsResponse = await eventsAPI.getAll();
      setEvents(eventsResponse.data.data);
      
    } catch (error) {
      setBackendStatus('‚ùå Backend Connection Failed');
      console.error('Connection error:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={styles.container}>
        <h3>üîó Testing Connection...</h3>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <h3>üîó Backend Connection Status</h3>
      <p>{backendStatus}</p>
      <p>üìä Events in Database: {events.length}</p>
      
      {events.length > 0 && (
        <div style={styles.eventsList}>
          <h4>üé™ Real Events from MongoDB:</h4>
          {events.map(event => (
            <div key={event._id} style={styles.eventCard}>
              <strong>{event.title}</strong>
              <p>{event.description}</p>
              <small>üìç {event.venue} | üóìÔ∏è {new Date(event.date).toLocaleDateString()}</small>
            </div>
          ))}
        </div>
      )}
      
      <button onClick={testConnection} style={styles.refreshBtn}>
        üîÑ Refresh Data
      </button>
    </div>
  );
};

const styles = {
  container: {
    background: 'rgba(255,255,255,0.1)',
    padding: '20px',
    borderRadius: '10px',
    margin: '20px 0',
    color: 'white',
    textAlign: 'center'
  },
  eventsList: {
    marginTop: '15px'
  },
  eventCard: {
    background: 'rgba(255,255,255,0.9)',
    margin: '10px 0',
    padding: '15px',
    borderRadius: '8px',
    color: '#333',
    textAlign: 'left'
  },
  refreshBtn: {
    background: '#667eea',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
    marginTop: '10px'
  }
};

export default TestConnection;
