import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';


const Register = () => {
  const [formData, setFormData] = useState({
    name: '', email: '', password: '', phone: '', department: '', year: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    console.log('üìù Registering user with data:', formData);
    const result = await register(formData);
    console.log('üìù Registration result:', result);
    if (result.success) {
      setMessage('‚úÖ Registration successful! Redirecting...');
      setTimeout(() => {
        navigate('/', { replace: true });
      }, 1000);
    } else {
      setMessage('‚ùå ' + result.message);
    }
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <div className="auth-logo">EventHub</div>
          <h2 className="auth-title">Create your account</h2>
          <p className="auth-subtitle">Join events, manage registrations, and discover experiences.</p>
        </div>

        <div>
          <form onSubmit={handleRegister} className="auth-form">
            <div>
              <label className="form-label">Full name</label>
              <input className="form-input" type="text" name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} required />
            </div>

            <div>
              <label className="form-label">Email</label>
              <input className="form-input" type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
            </div>

            <div className="form-row">
              <div>
                <label className="form-label">Password</label>
                <input className="form-input" type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
              </div>
              <div>
                <label className="form-label">Phone</label>
                <input className="form-input" type="tel" name="phone" placeholder="Phone" value={formData.phone} onChange={handleChange} />
              </div>
            </div>

            <div className="form-row">
              <div>
                <label className="form-label">Department</label>
                <input className="form-input" type="text" name="department" placeholder="Department" value={formData.department} onChange={handleChange} />
              </div>
              <div>
                <label className="form-label">Year</label>
                <select className="form-input" name="year" value={formData.year} onChange={handleChange}>
                  <option value="">Select Year</option>
                  <option value="1st Year">1st Year</option>
                  <option value="2nd Year">2nd Year</option>
                  <option value="3rd Year">3rd Year</option>
                  <option value="4th Year">4th Year</option>
                </select>
              </div>
            </div>

            <button type="submit" className="auth-btn" disabled={loading}>
              {loading ? 'Registering...' : 'Create account'}
            </button>
          </form>

          {message && <p className={`auth-message ${message.startsWith('‚ùå') ? 'error' : 'success'}`} style={{marginTop:12}}>{message.replace(/^‚úÖ |^‚ùå /,'')}</p>}

          <p className="muted" style={{marginTop:14}}>
            Already have an account? <span style={{color:'var(--accent-1)',cursor:'pointer',fontWeight:700}} onClick={() => navigate('/login')}>Sign in</span>
          </p>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: { display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh', padding: '20px' },
  card: { background: 'rgba(255,255,255,0.95)', padding: '40px', borderRadius: '15px', boxShadow: '0 20px 60px rgba(0,0,0,0.1)', maxWidth: '400px', width: '100%', textAlign: 'center' },
  title: { color: '#333', marginBottom: '30px', fontSize: '28px' },
  form: { display: 'flex', flexDirection: 'column', gap: '15px' },
  input: { padding: '12px 16px', border: '2px solid #e1e5e9', borderRadius: '8px', fontSize: '16px' },
  button: { padding: '14px', background: 'linear-gradient(135deg, #667eea, #764ba2)', color: 'white', border: 'none', borderRadius: '8px', fontSize: '16px', fontWeight: '600', cursor: 'pointer', marginTop: '10px' },
  link: { color: '#666', marginTop: '20px' },
  linkText: { color: '#667eea', cursor: 'pointer', fontWeight: '600', marginLeft: '5px' }
};

export default Register;
