import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { eventsAPI, registrationsAPI } from '../services/api';

const Events = () => {
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadEvents();
  }, []);

  const loadEvents = async () => {
    try {
      const response = await eventsAPI.getAll();
      setEvents(response.data.data);
    } catch (error) {
      setError('Failed to load events');
      console.error('Error loading events:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (eventId, eventTitle) => {
    try {
      await registrationsAPI.register(eventId);
      alert(`üéâ Successfully registered for: ${eventTitle}`);
      loadEvents(); // Refresh events to update participant count
    } catch (error) {
      alert(`‚ùå Registration failed: ${error.response?.data?.message || 'Please try again'}`);
    }
  };

  if (loading) {
    return (
      <div style={styles.container}>
        <div style={styles.loading}>Loading events from database...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={styles.container}>
        <div style={styles.error}>{error}</div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>All Events from Database</h1>
      <p style={styles.subtitle}>Real data loaded from MongoDB</p>

      {events.length === 0 ? (
        <div style={styles.noEvents}>
          <h3>No events found in database</h3>
          <p>Events will appear here once they are added to the database.</p>
        </div>
      ) : (
        <div style={styles.eventsGrid}>
          {events.map(event => (
            <div key={event._id} style={styles.eventCard}>
              <h3 style={styles.eventTitle}>{event.title}</h3>
              <p style={styles.eventDescription}>{event.description}</p>
              
              <div style={styles.eventDetails}>
                <p><strong>üìÖ Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
                <p><strong>‚è∞ Time:</strong> {event.time || 'To be announced'}</p>
                <p><strong>üìç Venue:</strong> {event.venue}</p>
                <p><strong>üë• Participants:</strong> {event.currentParticipants || 0}/{event.maxParticipants || 'Unlimited'}</p>
                {event.registrationFee > 0 && (
                  <p><strong>üí∞ Fee:</strong> ‚Çπ{event.registrationFee}</p>
                )}
              </div>

              <button
                style={styles.registerBtn}
                onClick={() => handleRegister(event._id, event.title)}
              >
                üìù Register Now
              </button>
            </div>
          ))}
        </div>
      )}

      <button style={styles.backBtn} onClick={() => navigate('/')}>
        ‚Üê Back to Home
      </button>
    </div>
  );
};

const styles = {
  container: { maxWidth: '1200px', margin: '0 auto', padding: '40px 20px', minHeight: '80vh' },
  title: { textAlign: 'center', color: 'white', fontSize: '3rem', marginBottom: '10px' },
  subtitle: { textAlign: 'center', color: 'rgba(255,255,255,0.8)', fontSize: '1.2rem', marginBottom: '40px' },
  loading: { textAlign: 'center', color: 'white', fontSize: '1.2rem', padding: '60px' },
  error: { textAlign: 'center', color: '#ff6b6b', fontSize: '1.2rem', padding: '60px' },
  noEvents: { textAlign: 'center', color: 'white', background: 'rgba(255,255,255,0.1)', padding: '60px', borderRadius: '15px' },
  eventsGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', gap: '30px', marginBottom: '40px' },
  eventCard: { background: 'rgba(255,255,255,0.95)', padding: '25px', borderRadius: '15px', boxShadow: '0 10px 40px rgba(0,0,0,0.1)' },
  eventTitle: { color: '#333', marginBottom: '15px', fontSize: '1.4rem' },
  eventDescription: { color: '#666', marginBottom: '20px', lineHeight: '1.5' },
  eventDetails: { color: '#333', marginBottom: '20px' },
  registerBtn: { 
    background: 'linear-gradient(135deg, #667eea, #764ba2)', 
    color: 'white', 
    border: 'none', 
    padding: '12px 24px', 
    borderRadius: '8px', 
    cursor: 'pointer', 
    fontSize: '16px', 
    fontWeight: '600', 
    width: '100%',
    transition: 'all 0.3s ease'
  },
  backBtn: { 
    background: '#6c757d', 
    color: 'white', 
    border: 'none', 
    padding: '12px 24px', 
    borderRadius: '8px', 
    cursor: 'pointer', 
    fontSize: '16px', 
    margin: '0 auto', 
    display: 'block' 
  }
};

export default Events;
